"""Balanceamento dos Dados"""

import pandas as pd
import matplotlib.pyplot as plt

data = pd.DataFrame({
    'Resposta': ['Sim'] * 10 + ['Não'] * 90
})

contagem = data['Resposta'].value_counts()
print(contagem)

contagem.plot(kind='bar', color=['skyblue', 'lightcoral'])
plt.title('Base de Dados Desbalanceada')
plt.xlabel('Classe')
plt.ylabel('Quantidade')
plt.xticks(rotation=0)
plt.show()

ohe = OneHotEncoder(sparse_output=False, dtype=int)
ohe_mat = ohe.fit_transform(data[['Resposta']])
ohe_cols = ohe.get_feature_names_out(['Resposta'])

df_onehot = pd.DataFrame(ohe_mat, columns=ohe_cols)
print(df_onehot.head())

"""SMOTE"""

from imblearn.over_sampling import SMOTE
from collections import Counter

X = df_onehot[['Resposta_Não']]
y = df_onehot['Resposta_Sim']

sm = SMOTE(random_state=42)
X_res, y_res = sm.fit_resample(X, y)

contagem = Counter(y_res)
print("Após SMOTE:", contagem)

import matplotlib.pyplot as plt
plt.bar(['Não', 'Sim'], list(contagem.values()), color=['lightcoral', 'skyblue'])
plt.title('Após SMOTE (Classes Balanceadas)')
plt.xlabel('Classe')
plt.ylabel('Quantidade')
plt.xticks(rotation=0)
plt.show()

"""Balanced"""

X = df_onehot[['Resposta_Não']]
y = df_onehot['Resposta_Sim']

X_train, X_test, y_train, y_test = train_test_split(X, y, stratify=y, random_state=42)

model = LogisticRegression(class_weight='balanced', max_iter=1000)
model.fit(X_train, y_train)

y_pred = model.predict(X_test)
print(classification_report(y_test, y_pred, target_names=['Não', 'Sim']))

